import React, { Component, Fragment } from "react";
import cookie from 'react-cookies'
import { Redirect } from 'react-router-dom';
import Title from "../components/Title";
import API from "../utils/API";

class Secure extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cookie: null,
      loading: true
    };
  }

  componentDidMount() {
    this.validateCookie();
  }

  validateCookie() {
    const cookieValue = cookie.load('connect.sid');
    API.validateCookie(cookieValue)
      .then(res => {
        if (res.status === 200) {
          this.setState({ cookie: cookieValue, loading: false });
        } else {
          this.setState({ loading: false });
        }
      })
      .catch(err => this.setState({ loading: false }))
  }

  handleLogout () {
    API.logout()
      .then(res => this.props.history.push('/login'))
      .catch(err => console.error(err));
  }

  render() {
    if (this.state.loading) {
      return <div>Loading...</div>;
    }
    if (!this.state.cookie) {
      return <Redirect to='/login' />
    }
    return (
      <Fragment>
        <Title>Log In Successful!</Title>
        <button id="logout" onClick={this.handleLogout} className="btn">LOG OUT</button>
      </Fragment>
    );
  }
}

export default Secure;
